import logo from './logo.svg';
import './App.css';

import React, { useState, useRef } from "react";
import ColorThief from "colorthief";

function App() {
  const [dominantColor, setDominantColor] = useState(null);
  const [image, setImage] = useState(null);
  const imgRef = useRef(null);

  const handleImageChange = (event) => {
    const file = event.target.files[0];
    const imageURL = URL.createObjectURL(file);
    setImage(imageURL);
  };

  const extractColor = () => {
    const colorThief = new ColorThief();
    const imgElement = imgRef.current;

    if (imgElement.complete) {
      const color = colorThief.getColor(imgElement);
      setDominantColor(color);
    } else {
      imgElement.onload = () => {
        const color = colorThief.getColor(imgElement);
        setDominantColor(color);
      };
    }
  };

  return (
    <div style={{ textAlign: "center", padding: "50px" }}>
      <h1>Dominant Color Finder</h1>

      <input type="file" accept="image/*" onChange={handleImageChange} />

      {image && (
        <div>
          <img
            src={image}
            ref={imgRef}
            crossOrigin="anonymous"
            alt="Uploaded"
            style={{ maxWidth: "500px", margin: "20px auto" }}
          />
          <button onClick={extractColor}>Find Dominant Color</button>
        </div>
      )}

      {dominantColor && (
        <div>
          <h2>Dominant Color:</h2>
          <div
            style={{
              backgroundColor: `rgb(${dominantColor[0]}, ${dominantColor[1]}, ${dominantColor[2]})`,
              width: "100px",
              height: "100px",
              margin: "0 auto",
            }}
          />
          <p>RGB: {dominantColor.join(", ")}</p>
        </div>
      )}
    </div>
  );
}

export default App;
